if ( ! defined( 'WPINC' ) ) {
	die;
}

get_header(); ?>

<?php

	$thumb 			= get_post_thumbnail_id($post->ID);
	$img_url 		= wp_get_attachment_url( $thumb,'full');
	$enroll 		= get_post_meta(get_the_ID(), 'Enroll Now', true);
	$start 			= get_post_meta(get_the_ID(), 'Course Start', true);
	$length 		= get_post_meta(get_the_ID(), 'Course Length', true);
	$time 			= get_post_meta(get_the_ID(), 'Time Requirement', true);
	$profname 		= get_post_meta(get_the_ID(), 'Professor Name', true);
	$profbio		= get_post_meta(get_the_ID(), 'Professor Bio', true);
	$profpic		= get_post_meta(get_the_ID(), 'Professor Pic', true);
	$certificate	= get_post_meta(get_the_ID(), 'Certificate', true);
	$tuition		= get_post_meta(get_the_ID(), 'Tuition', true);

	if($start != ''){
		$start 		= date("M j, Y", strtotime($start));

		//return $start;
	}

?>

	<div id="content-header" style="background-image: url(<?php echo($img_url); ?>);">
		<h2><?php echo(get_the_title()); ?></h2>
		<p><a href="<?php echo($enroll); ?>" class="button enroll standalone red">Enroll Now</a></p>
	</div>
	<div id="course-info">
		<ul>
			<li class="start"><span>Course Start</span><?php echo($start); ?></li>
			<li class="professor"><span>Professor</span><?php echo($profname); ?></li>
			<li class="workload"><span>Workload</span><?php echo($time); ?> Hours</li>
			<li class="certificate"><span>Certificate Offered</span><?php echo($certificate) ?></li>
			<li class="tuition"><span>Tuition</span><?php echo('$' . $tuition); ?></li>
		</ul>
	</div>

	<div id="content" class="content-area">
			<main id="main" class="site-main" role="main">

				<?php while ( have_posts() ) : the_post(); ?>

					<?php echo(the_content()); ?>

				<?php endwhile; // end of the loop. ?>
				
				<div id="professor">
					<div class="headshot">
						<img src="<?php echo($profpic); ?>" alt="Picture of <?php echo($profname); ?>" />
					</div>
					<div class="professorinfo">
						<h4><?php echo($profname); ?></h4>
						<p><?php echo($profbio); ?></p>
					</div>
					<div class="clearfix">&nbsp;</div>
				</div>
				<div class="enroll-container"><a href="<?php echo($enroll); ?>" class="button enroll standalone red">Enroll Now</a></div>

			</main><!-- #main -->

			<?php get_sidebar(); ?>
	</div><!-- #content -->
<?php get_footer(); ?>
